import React, {Component} from 'react';
import './App.css';
import './Person/person.css'
import './TimeInputs/timeinputs.css'
import './Total/total.css'
import './WorkItem/workitem.css'
import 'react-datepicker/dist/react-datepicker.css'
import Person from './Person/person';
import TimeInput from './TimeInputs/timesinputs'
import Total from './Total/total'
import WorkItem from './WorkItem/workitem'
import DatePicker from "react-datepicker"


class App extends Component{
  date = new Date();
  state = {
    persons: [
    { id: '1', name: 'Maximilian', age: 28 },
    { id: '2', name: 'Manu', age: 29 },
    { id: '3', name: 'Stephanie', age: 30 }
    ],
    otherState: 'some other values',
    showPersons: false,
    timeInputs: [
      { id: '1', name: 'Starting time' },
      { id: '2', name: 'Finishing time' },
      { id: '3', name: 'Starting lunch break' },
      { id: '4', name: 'Finishing lunch break' }
    ],
    totalCards: [
      {id: '1', value: 'Total worked:', color: '#85aded'},
      {id: '2', value: 'Number of workdays:', color: '#b00538'},
      {id: '3', value: 'Total lunchbreaks: ', color: '#b3b00e'}
    ],
    workItems: [
      {id: '1', date: '10/10/2020', workedFrom: '8 AM', workedTo: '7 PM', totalWorked: '9 hours', lunchBreakFrom: '9 AM', lunchBreakTo: '9:30 AM', totalLunchBreak: '30 minutes'}
    ],
    date: this.date
  }

  deletePersonHandler = (index) =>{
    //moze drugi nacin je ES6. linije kopiraju state u const
    //const persons = this.state.persons.slice();
    const persons = [...this.state.persons];
    persons.splice(index, 1);
    this.setState({persons:persons})
  }

  nameChangedHandler = (event, id) =>{
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === id;
    });

    const person = {
      ...this.state.persons[personIndex]
    };
    person.name = event.target.value;

    const persons = [...this.state.persons];
    persons[personIndex] = person;

    this.setState({persons:persons});
  }

  dateChangedHandler = (date) =>{
    this.setState({date: date});
  }

  togglePersonsHandler = () =>{
    const doesShow = this.state.showPersons;
    this.setState({showPersons: true});
  }

  render(){

    const style = {
      backgroundColor: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    };

    let persons = null;

    if(this.state.showPersons){
      persons = (
        <div>
        {this.state.persons.map((person,index) => {
          return <Person 
          click={this.deletePersonHandler.bind(this, index)}
          name={person.name}
          age={person.age}
          key={person.id}
          changed={(event) => this.nameChangedHandler(event, person.id)}/> 
        })}
      </div>)
    }

    let timeInputs = (
      <div>
        {
          this.state.timeInputs.map((input, index) => {
            return <TimeInput 
              key={input.id}
              name={input.name}/>
          })
        }
      </div>
    )

    let cardsTotal = (
      <div>
        {
          this.state.totalCards.map((card, index) => {
            return <Total 
              key={card.id}
              name={card.value}
              color={card.color}/>
          })
        }
      </div>
    )

    let workItems = (
      <div>
      {
        this.state.workItems.map((item, index) => {
          return <WorkItem
            key={item.id}
            date={item.date}
            workedFrom={item.workedFrom}
            workedTo={item.workedTo}
            totalWorked={item.totalWorked}
            lunchBreakFrom={item.lunchBreakFrom}
            lunchBreakTo={item.lunchBreakTo}
            totalLunchBreak={item.totalLunchBreak}
            />
        })
      }
    </div>
    )

    const setSelectedDate = useState(null);
    let datePicker = (
      <div>
        return <DatePicker selected={this.state.date}
                    onChange={date => setSelectedDate(date)} />
      </div>
    )

    return (
      <div className="App">
        <h1>Title</h1>
        <p>working p</p>
        <button
          style={style}
          onClick={this.togglePersonsHandler}>Hide/unhide</button>
        {persons}
        <div className="header-img">
        <div className="datepicker-container-outer">
          <div className="datepicker-container">
            {datePicker}
          </div>
        </div>
        <div className="input-container-outer">
          <div className="input-container">{timeInputs}</div>
        </div>
        <div className="total-container-outer">
          <div className="total-container">{cardsTotal}</div></div>
        </div>
        <div className="work-items-container-outer">
          <div className="work-items-container">
            {workItems}
          </div>
        </div>
      </div>
      );
    }
  }
  export default App;
